// Alternative scraping using RapidAPI Instagram services
// This method doesn't require Instagram login

const url = $input.first().json.instagram_url;
const username = url.match(/instagram\.com\/([^\/\?]+)/)?.[1];

if (!username) {
  throw new Error('Invalid Instagram URL');
}

return [{
  json: {
    username: username,
    url: url,
    timestamp: new Date().toISOString(),
    services_to_use: [
      'rapidapi_instagram',
      'brightdata_instagram',
      'proxycrawl_instagram'
    ]
  }
}];

// Extract followers and find similar accounts
const profile = $input.first().json.data;
const username = profile.username;

// Get followers sample for analysis
return [{
  json: {
    profile: {
      username: profile.username,
      full_name: profile.full_name,
      biography: profile.biography,
      follower_count: profile.follower_count,
      following_count: profile.following_count,
      media_count: profile.media_count,
      is_verified: profile.is_verified,
      category: profile.category_name,
      external_url: profile.external_url
    },
    next_step: 'fetch_followers'
  }
}];

// Analyze followers to find competitor patterns
const followers = $input.first().json.data.items || [];
const mainProfile = $node["Process Profile Data"].json.profile;

// Analyze follower accounts to find similar businesses
const potentialCompetitors = [];
const businessKeywords = ['shop', 'store', 'brand', 'boutique', 'company', 'official', 'studio'];

followers.forEach(follower => {
  // Check if follower might be a business account
  const bio = (follower.biography || '').toLowerCase();
  const username = (follower.username || '').toLowerCase();
  const isBusinessLikely = 
    follower.is_verified || 
    follower.follower_count > 5000 ||
    businessKeywords.some(keyword => bio.includes(keyword) || username.includes(keyword));

  if (isBusinessLikely) {
    potentialCompetitors.push({
      username: follower.username,
      full_name: follower.full_name,
      follower_count: follower.follower_count,
      is_verified: follower.is_verified,
      profile_pic_url: follower.profile_pic_url,
      score: calculateCompetitorScore(follower, mainProfile)
    });
  }
});

// Sort by score and take top candidates
const topCompetitors = potentialCompetitors
  .sort((a, b) => b.score - a.score)
  .slice(0, 30);

return [{
  json: {
    main_profile: mainProfile,
    competitor_candidates: topCompetitors,
    total_analyzed: followers.length
  }
}];

function calculateCompetitorScore(account, mainProfile) {
  let score = 0;

  // Similar follower count (within 50-200% range)
  const followerRatio = account.follower_count / mainProfile.follower_count;
  if (followerRatio >= 0.5 && followerRatio <= 2) {
    score += 30;
  }

  // Verified accounts get bonus
  if (account.is_verified) score += 20;

  // Active accounts (has posts)
  if (account.media_count > 10) score += 15;

  // Engagement indicators
  if (account.follower_count > 10000) score += 10;
  if (account.follower_count > 100000) score += 15;

  return score;
}

// Batch process competitor candidates to get detailed info
const candidates = $input.first().json.competitor_candidates;
const batchSize = 5; // Process 5 at a time to avoid rate limits
const batches = [];

for (let i = 0; i < candidates.length; i += batchSize) {
  batches.push(candidates.slice(i, i + batchSize));
}

return batches.map(batch => ({
  json: {
    batch: batch,
    batch_number: batches.indexOf(batch) + 1,
    total_batches: batches.length
  }
}));

// Enhanced geo-location detection
const competitors = $input.all();
const geoPatterns = {
  'USA': {
    keywords: ['USA', 'United States', 'America', 'US', 'üá∫üá∏'],
    cities: ['New York', 'Los Angeles', 'Chicago', 'Houston', 'Phoenix', 'Philadelphia', 'San Antonio', 'San Diego', 'Dallas', 'San Jose', 'NYC', 'LA', 'SF'],
    domains: ['.com', '.us'],
    timezone_indicators: ['EST', 'CST', 'MST', 'PST', 'EDT', 'CDT', 'MDT', 'PDT']
  },
  'UK': {
    keywords: ['UK', 'United Kingdom', 'Britain', 'England', 'Scotland', 'Wales', 'üá¨üáß'],
    cities: ['London', 'Manchester', 'Birmingham', 'Leeds', 'Glasgow', 'Liverpool', 'Bristol', 'Sheffield', 'Edinburgh', 'Leicester'],
    domains: ['.co.uk', '.uk'],
    timezone_indicators: ['GMT', 'BST']
  },
  'EU': {
    keywords: ['Europe', 'EU', 'European', 'üá™üá∫'],
    cities: ['Paris', 'Berlin', 'Madrid', 'Rome', 'Barcelona', 'Munich', 'Milan', 'Warsaw', 'Vienna', 'Hamburg', 'Budapest', 'Amsterdam', 'Prague', 'Brussels', 'Stockholm'],
    domains: ['.eu', '.de', '.fr', '.it', '.es', '.nl', '.pl', '.be', '.se'],
    timezone_indicators: ['CET', 'CEST', 'EET', 'EEST']
  },
  'RU': {
    keywords: ['Russia', '–†–æ—Å—Å–∏—è', '–†–§', 'Russian', 'üá∑üá∫'],
    cities: ['Moscow', '–ú–æ—Å–∫–≤–∞', 'Saint Petersburg', '–°–∞–Ω–∫—Ç-–ü–µ—Ç–µ—Ä–±—É—Ä–≥', '–°–ü–±', 'Novosibirsk', '–ù–æ–≤–æ—Å–∏–±–∏—Ä—Å–∫', 'Yekaterinburg', '–ï–∫–∞—Ç–µ—Ä–∏–Ω–±—É—Ä–≥'],
    domains: ['.ru', '.—Ä—Ñ'],
    timezone_indicators: ['MSK', '–ú–°–ö']
  }
};

const geoTaggedCompetitors = competitors.map(item => {
  const data = item.json;
  let detectedRegion = 'Unknown';
  let confidence = 0;

  // Combine all text fields for analysis
  const textToAnalyze = `
    ${data.username || ''} 
    ${data.full_name || ''} 
    ${data.biography || ''} 
    ${data.external_url || ''} 
    ${data.category_name || ''}
  `.toLowerCase();

  // Check each region
  const regionScores = {};

  for (const [region, patterns] of Object.entries(geoPatterns)) {
    let score = 0;

    // Check keywords
    patterns.keywords.forEach(keyword => {
      if (textToAnalyze.includes(keyword.toLowerCase())) {
        score += 10;
      }
    });

    // Check cities
    patterns.cities.forEach(city => {
      if (textToAnalyze.includes(city.toLowerCase())) {
        score += 15;
      }
    });

    // Check domains
    patterns.domains.forEach(domain => {
      if (textToAnalyze.includes(domain)) {
        score += 8;
      }
    });

    regionScores[region] = score;
  }

  // Find highest scoring region
  const topRegion = Object.entries(regionScores)
    .sort((a, b) => b[1] - a[1])[0];

  if (topRegion[1] > 0) {
    detectedRegion = topRegion[0];
    confidence = Math.min(topRegion[1], 100);
  }

  return {
    json: {
      ...data,
      detected_region: detectedRegion,
      region_confidence: confidence,
      analysis_timestamp: new Date().toISOString()
    }
  };
});

return geoTaggedCompetitors;

// Create final report with top 100 competitors
const allCompetitors = $input.all().map(item => item.json);

// Group by region
const byRegion = {
  'USA': [],
  'UK': [],
  'EU': [],
  'RU': [],
  'Unknown': []
};

allCompetitors.forEach(competitor => {
  const region = competitor.detected_region || 'Unknown';
  if (byRegion[region]) {
    byRegion[region].push(competitor);
  }
});

// Sort each region by follower count and take top 25
Object.keys(byRegion).forEach(region => {
  byRegion[region] = byRegion[region]
    .sort((a, b) => (b.follower_count || 0) - (a.follower_count || 0))
    .slice(0, 25);
});

// Create global top 100
const top100Global = allCompetitors
  .sort((a, b) => (b.follower_count || 0) - (a.follower_count || 0))
  .slice(0, 100);

// Generate insights
const insights = {
  total_analyzed: allCompetitors.length,
  by_region_count: Object.entries(byRegion).map(([region, accounts]) => ({
    region,
    count: accounts.length,
    avg_followers: Math.round(
      accounts.reduce((sum, a) => sum + (a.follower_count || 0), 0) / (accounts.length || 1)
    )
  })),
  verified_accounts: allCompetitors.filter(a => a.is_verified).length,
  avg_follower_count: Math.round(
    allCompetitors.reduce((sum, a) => sum + (a.follower_count || 0), 0) / allCompetitors.length
  )
};

const report = {
  report_id: `trend_${Date.now()}`,
  generated_at: new Date().toISOString(),

  summary: {
    total_competitors_found: allCompetitors.length,
    insights: insights
  },

  top_100_global: top100Global.map((c, index) => ({
    rank: index + 1,
    username: c.username,
    full_name: c.full_name,
    followers: c.follower_count,
    region: c.detected_region,
    is_verified: c.is_verified,
    profile_url: `https://instagram.com/${c.username}`
  })),

  by_region: Object.entries(byRegion).map(([region, accounts]) => ({
    region,
    competitors: accounts.map((c, index) => ({
      rank: index + 1,
      username: c.username,
      followers: c.follower_count,
      is_verified: c.is_verified
    }))
  })),

  recommendations: [
    'Focus on accounts with 10K-100K followers for partnership opportunities',
    'Monitor verified accounts for industry trends',
    `Primary market opportunity in ${insights.by_region_count[0].region} with ${insights.by_region_count[0].count} active competitors`,
    'Consider content strategies used by top 10 global competitors'
  ]
};

return [{ json: report }];
